import React from 'react';
import Navbar from '../../components/Navbar/Navbar';
import Globe from '../../components/Globe/Globe'; // Import the Globe component
import '../../components/styles/home.css'; // Ensure you have a corresponding CSS for general page styling

const Home = () => {
  return (
    <div className="home">
      <Navbar />
      <main className="home-content">
        {/* Main content area - includes the Globe component */}
        <section className="intro">
          <p>Explore real-time global issues on our interactive globe and join our mission to spread love by solving one another's problems, inspired by Jesus's teachings of love. "A new command I give you: Love one another. As I have loved you, so you must love one another." - John 13:34</p>
        </section>
        {/* Render the Globe component here */}
        <Globe />
        <section className="features">
          <p><a href="/solutions" style={{ color: 'inherit', textDecoration: 'none' }}>Discover innovative solutions addressing global problems.</a></p>
        </section>
      </main>
    </div>
  );
};

export default Home;
