/* global Cesium */
import React, { useEffect, useRef } from 'react';

const Globe = () => {
    const globeRef = useRef(null);
    const viewerRef = useRef(null);
    const animationIdRef = useRef(null);  // To store the animation frame ID

    useEffect(() => {
        Cesium.Ion.defaultAccessToken = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJqdGkiOiJkMWY1MGUyNC0wNjFkLTQ1YWMtYTBhNi1mYTRkMTAzNWYzOGEiLCJpZCI6MjYzODA1LCJpYXQiOjE3MzQ2Nzk0ODd9.OvjRSddy3Mt1P1rOGIFKOQQcxIqTX2i7sM1Ha4s7_qs';

        if (globeRef.current && !viewerRef.current) {
            try {
                viewerRef.current = new Cesium.Viewer(globeRef.current, {
                    imageryProvider: new Cesium.IonImageryProvider({ assetId: 2 }),
                    baseLayerPicker: false,
                    geocoder: false,
                    homeButton: false,
                    infoBox: false,
                    navigationHelpButton: false,
                    sceneModePicker: false,
                    timeline: false,
                    animation: false,
                    requestRenderMode: true,
                    maximumRenderTimeChange: Infinity,
                });

                // Set initial view
                viewerRef.current.scene.camera.setView({
                    destination: Cesium.Cartesian3.fromDegrees(-74.0707383, 40.7117244, 10000000), // Keep original altitude
                    orientation: {
                        heading: Cesium.Math.toRadians(0),  // North-aligned
                        pitch: Cesium.Math.toRadians(-90),  // Directly top-down view
                        roll: 0.0
                    }
                });

                // Continuous rotation
                const rotateEarth = () => {
                    const spinRate = 0.01;
                    const delta = spinRate / 60;  // Assuming 60 FPS for rotation rate
                    viewerRef.current.scene.camera.rotate(Cesium.Cartesian3.UNIT_Z, -delta);
                    animationIdRef.current = requestAnimationFrame(rotateEarth);
                };

                rotateEarth();

                // Event handlers to stop/start rotation on user interaction
                viewerRef.current.scene.screenSpaceCameraController.inertiaSpin = 0;
                viewerRef.current.scene.screenSpaceCameraController.inertiaTranslate = 0;
                viewerRef.current.scene.screenSpaceCameraController.inertiaZoom = 0;

                const stopRotation = () => {
                    if (animationIdRef.current) {
                        cancelAnimationFrame(animationIdRef.current);
                        animationIdRef.current = null;
                    }
                };

                const startRotation = () => {
                    if (!animationIdRef.current) {
                        rotateEarth();
                    }
                };

                // Listen for start and stop events
                viewerRef.current.canvas.addEventListener('pointerdown', stopRotation);
                viewerRef.current.canvas.addEventListener('pointerup', startRotation);
                viewerRef.current.canvas.addEventListener('wheel', stopRotation, {passive: true});
                viewerRef.current.canvas.addEventListener('wheel', startRotation, {passive: true, delay: 1000});

            } catch (error) {
                console.error('Failed to initialize Cesium viewer:', error);
            }
        }

        return () => {
            if (viewerRef.current) {
                viewerRef.current.destroy();
                viewerRef.current = null;
            }
        };
    }, []);

    // NEW CODE ADDED HERE: A button to manually stop rotation
    const handleStopRotationClick = () => {
        if (animationIdRef.current) {
            cancelAnimationFrame(animationIdRef.current);
            animationIdRef.current = null;
        }
    };

    return (
        <>
            {/* Button to manually stop rotation */}
            <div style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                zIndex: 9999
            }}>
                <button onClick={handleStopRotationClick} style={{
                    padding: '10px 15px',
                    fontSize: '14px',
                    cursor: 'pointer'
                }}>
                    Stop Rotation
                </button>
            </div>

            <div
                ref={globeRef}
                id="cesiumContainer"
                style={{ height: "100vh", width: "100vw" }}
            />
        </>
    );
};

export default Globe;
