// src/components/Globe/LocationInfoPanel.jsx
import React from 'react';
import PropTypes from 'prop-types';
import '../styles/LocationInfoPanel.css'; // Import the dedicated CSS file

const LocationInfoPanel = ({ locationInfo, loading, error, translate }) => {
  if (loading) {
    return (
      <div className="location-info-panel">
        <h2>{translate('locationDetails')}</h2>
        <p>{translate('loading')}</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="location-info-panel">
        <h2>{translate('locationDetails')}</h2>
        <p className="error-message">{`${translate('error')}: ${error}`}</p>
      </div>
    );
  }

  if (!locationInfo) {
    return (
      <div className="location-info-panel">
        <h2>{translate('locationDetails')}</h2>
        <p>{translate('unknown')}</p>
      </div>
    );
  }

  // Destructure with default values to ensure no undefined properties are accessed
  const {
    address = 'Unknown',
    latitude = 'Unknown',
    longitude = 'Unknown',
    height = 'Unknown',
    zoomLevel = 'Unknown',
  } = locationInfo;

  return (
    <div className="location-info-panel">
      <h2>{translate('locationDetails')}</h2>
      <p>
        <strong>{translate('address')}:</strong> {address}
      </p>
      <p>
        <strong>{translate('latitude')}:</strong> {latitude}
      </p>
      <p>
        <strong>{translate('longitude')}:</strong> {longitude}
      </p>
      <p>
        <strong>{translate('height')}:</strong> {height} meters
      </p>
      <p>
        <strong>{translate('zoomLevel')}:</strong> {zoomLevel}
      </p>
    </div>
  );
};

LocationInfoPanel.propTypes = {
  locationInfo: PropTypes.shape({
    address: PropTypes.string,
    latitude: PropTypes.string,
    longitude: PropTypes.string,
    height: PropTypes.string,
    zoomLevel: PropTypes.string,
  }),
  loading: PropTypes.bool.isRequired,
  error: PropTypes.string,
  translate: PropTypes.func.isRequired,
};

LocationInfoPanel.defaultProps = {
  locationInfo: null,
  error: null,
};

export default LocationInfoPanel;
