// src/components/Globe/Controls.jsx
import React from 'react';
import PropTypes from 'prop-types';
import '../styles/Controls.css'; // Import the dedicated CSS file

/**
 * Controls Component
 *
 * Renders UI controls for managing the globe's rotation.
 *
 * Props:
 * - isRotating: Boolean indicating if the globe is currently rotating.
 * - onStartRotation: Function to start the globe's rotation.
 * - onStopRotation: Function to stop the globe's rotation.
 * - translate: Function to handle language translations.
 */
const Controls = ({ isRotating, onStartRotation, onStopRotation, translate }) => {
  return (
    <div className="controls-container">
      {isRotating ? (
        <button
          className="stop-rotation-button"
          onClick={onStopRotation}
          aria-label={translate('stopRotation')}
        >
          {translate('stopRotation')}
        </button>
      ) : (
        <button
          className="start-rotation-button"
          onClick={onStartRotation}
          aria-label={translate('startRotation')}
        >
          {translate('startRotation')}
        </button>
      )}
    </div>
  );
};

Controls.propTypes = {
  isRotating: PropTypes.bool.isRequired,
  onStartRotation: PropTypes.func.isRequired,
  onStopRotation: PropTypes.func.isRequired,
  translate: PropTypes.func.isRequired,
};

export default Controls;
