import React, { useEffect, useRef } from 'react';

const Globe = () => {
    const globeRef = useRef(null);

    useEffect(() => {
        Cesium.Ion.defaultAccessToken = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJqdGkiOiJkMWY1MGUyNC0wNjFkLTQ1YWMtYTBhNi1mYTRkMTAzNWYzOGEiLCJpZCI6MjYzODA1LCJpYXQiOjE3MzQ2Nzk0ODd9.OvjRSddy3Mt1P1rOGIFKOQQcxIqTX2i7sM1Ha4s7_qs';

        if (globeRef.current) {
            const viewer = new Cesium.Viewer(globeRef.current, {
                imageryProvider: new Cesium.IonImageryProvider({ assetId: 2 }),
                baseLayerPicker: false,
                geocoder: false,
                homeButton: false,
                infoBox: false,
                navigationHelpButton: false,
                sceneModePicker: false,
                timeline: false,
                animation: false,
                requestRenderMode: true,
                maximumRenderTimeChange: Infinity,
            });

            viewer.scene.globe.maximumScreenSpaceError = 2; // Adjust for performance

            viewer.scene.camera.setView({
                destination: Cesium.Cartesian3.fromDegrees(-74.0707383, 40.7117244, 10000000),
                orientation: {
                    heading: Cesium.Math.toRadians(0),
                    pitch: Cesium.Math.toRadians(-90),
                    roll: 0.0
                }
            });

            // Continuous rotation
            const rotateEarth = () => {
                let previousTime = Date.now();
                const spinRate = 0.001;

                function update() {
                    const currentTime = Date.now();
                    const delta = (currentTime - previousTime) / 1000;
                    previousTime = currentTime;
                    viewer.scene.camera.rotate(Cesium.Cartesian3.UNIT_Z, -spinRate * delta);
                    requestAnimationFrame(update);
                }

                update();
            };

            rotateEarth();

            return () => {
                viewer.destroy();
            };
        }
    }, []);

    return <div ref={globeRef} id="cesiumContainer" style={{ height: "100vh", width: "100vw" }} />;
};

export default Globe;
