/* global Cesium */
import React, { useEffect, useRef } from 'react';

const Globe = () => {
    const globeRef = useRef(null);

    useEffect(() => {
        if (globeRef.current) {
            // Initialize the Cesium Viewer in the div with the given id
            const viewer = new Cesium.Viewer(globeRef.current, {
                imageryProvider: new Cesium.IonImageryProvider({ assetId: 2 }), // Default Cesium world imagery
                baseLayerPicker: false, // Disables the base layer picker
                geocoder: false,        // Disables the geocoder
                homeButton: false,      // Disables the home button
                infoBox: false,         // Disables the info box
                navigationHelpButton: false, // Disables the navigation help button
                sceneModePicker: false, // Disables the scene mode picker
                timeline: false,        // Disables the timeline
                animation: false,       // Disables the animation widget
            });

            // Start the globe with a view of the world
            viewer.scene.camera.setView({
                destination: Cesium.Cartesian3.fromDegrees(-74.0707383, 40.7117244, 10000000),
                orientation: {
                    heading: Cesium.Math.toRadians(0),
                    pitch: Cesium.Math.toRadians(-90),
                    roll: 0.0
                }
            });

            return () => {
                viewer.destroy();
            };
        }
    }, []);

    return <div ref={globeRef} id="cesiumContainer" style={{ height: "100vh", width: "100vw" }} />;
};

export default Globe;
