/* global Cesium */
import React, { useEffect, useRef, useState } from 'react';
import '../styles/Globe.css'; // Import the separate style file

const Globe = () => {
    const globeRef = useRef(null);
    const viewerRef = useRef(null);
    const animationIdRef = useRef(null);  // To store the animation frame ID

    // Track whether the globe is currently rotating
    const [isRotating, setIsRotating] = useState(true);

    // We'll reference this function when we want to start rotation again
    const rotateEarth = () => {
        // Prevent rotation logic if viewerRef.current is null or has been destroyed
        if (!viewerRef.current) return;

        const spinRate = 0.01;
        const delta = spinRate / 60;  // Assuming 60 FPS for rotation rate

        viewerRef.current.scene.camera.rotate(Cesium.Cartesian3.UNIT_Z, -delta);
        animationIdRef.current = requestAnimationFrame(rotateEarth);
    };

    useEffect(() => {
        Cesium.Ion.defaultAccessToken =
            'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJqdGkiOiJkMWY1MGUyNC0wNjFkLTQ1YWMtYTBhNi1mYTRkMTAzNWYzOGEiLCJpZCI6MjYzODA1LCJpYXQiOjE3MzQ2Nzk0ODd9.OvjRSddy3Mt1P1rOGIFKOQQcxIqTX2i7sM1Ha4s7_qs';

        if (globeRef.current && !viewerRef.current) {
            try {
                // Viewer initialization
                viewerRef.current = new Cesium.Viewer(globeRef.current, {
                    imageryProvider: new Cesium.IonImageryProvider({ assetId: 2 }),
                    baseLayerPicker: false,
                    geocoder: false,
                    homeButton: false,
                    infoBox: false,
                    navigationHelpButton: false,
                    sceneModePicker: false,
                    timeline: false,
                    animation: false,
                    requestRenderMode: true,
                    maximumRenderTimeChange: Infinity,
                });

                // Set initial view (top-down perspective)
                viewerRef.current.scene.camera.setView({
                    destination: Cesium.Cartesian3.fromDegrees(-74.0707383, 40.7117244, 10000000),
                    orientation: {
                        heading: Cesium.Math.toRadians(0),
                        pitch: Cesium.Math.toRadians(-90),
                        roll: 0.0
                    }
                });

                // Start the rotation immediately (only if isRotating is true)
                if (isRotating) {
                    rotateEarth();
                }

                // Configure scene interactions to stop rotation on user drag/zoom
                viewerRef.current.scene.screenSpaceCameraController.inertiaSpin = 0;
                viewerRef.current.scene.screenSpaceCameraController.inertiaTranslate = 0;
                viewerRef.current.scene.screenSpaceCameraController.inertiaZoom = 0;

                const stopRotation = () => {
                    if (animationIdRef.current) {
                        cancelAnimationFrame(animationIdRef.current);
                        animationIdRef.current = null;
                    }
                };

                const startRotation = () => {
                    if (!animationIdRef.current && isRotating) {
                        rotateEarth();
                    }
                };

                // Event listeners to stop rotation on pointer down/wheel
                // and restart on pointer up/wheel end
                viewerRef.current.canvas.addEventListener('pointerdown', stopRotation);
                viewerRef.current.canvas.addEventListener('pointerup', () => {
                    if (isRotating) {
                        startRotation();
                    }
                });
                viewerRef.current.canvas.addEventListener('wheel', stopRotation, { passive: true });
                viewerRef.current.canvas.addEventListener('wheel', () => {
                    if (isRotating) {
                        startRotation();
                    }
                }, { passive: true, delay: 1000 });

            } catch (error) {
                console.error('Failed to initialize Cesium viewer:', error);
            }
        }

        // Cleanup
        return () => {
            if (viewerRef.current) {
                viewerRef.current.destroy();
                viewerRef.current = null;
            }
        };
    }, [isRotating]); // Depend on isRotating, so the effect reruns if we toggle rotation

    // Toggle rotation with a button click
    const handleStopRotationClick = () => {
        if (isRotating) {
            // If it's rotating, cancel the animation & set state
            if (animationIdRef.current) {
                cancelAnimationFrame(animationIdRef.current);
                animationIdRef.current = null;
            }
            setIsRotating(false);
        } else {
            // Resume rotation if it was stopped
            // Make sure we have a viewer before rotating
            if (viewerRef.current) {
                rotateEarth();
            }
            setIsRotating(true);
        }
    };

    return (
        <>
            {/* Button to manually stop or resume rotation */}
            <div className="stop-rotation-button-container">
                <button
                    className="stop-rotation-button"
                    onClick={handleStopRotationClick}
                >
                    {isRotating ? 'Stop Rotation' : 'Resume Rotation'}
                </button>
            </div>

            <div
                ref={globeRef}
                id="cesiumContainer"
                style={{ height: "100vh", width: "100vw" }}
            />
        </>
    );
};

export default Globe;
