// src/components/Globe/GlobeViewer.jsx
import React, { useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import '../styles/GlobeViewer.css'; // Import the dedicated CSS file

/**
 * GlobeViewer Component
 *
 * Initializes the Cesium Viewer and manages its lifecycle.
 *
 * Props:
 * - onViewerInitialized: Callback function to pass the Cesium Viewer instance to the parent component.
 */
const GlobeViewer = ({ onViewerInitialized }) => {
  const globeRef = useRef(null);

  useEffect(() => {
    // Ensure Cesium is loaded
    if (!window.Cesium || !globeRef.current) {
      console.error('Cesium.js is not loaded. Please check your script tags.');
      return;
    }

    // Initialize Cesium Viewer
    const viewer = new window.Cesium.Viewer(globeRef.current, {
      terrainProvider: window.Cesium.createWorldTerrain(),
      imageryProvider: new window.Cesium.IonImageryProvider({ assetId: 2 }),
      baseLayerPicker: false,
      geocoder: false,
      homeButton: false,
      infoBox: false, // We'll handle info boxes manually
      navigationHelpButton: false,
      sceneModePicker: false,
      timeline: false,
      animation: false,
      fullscreenButton: true,
      requestRenderMode: true,
      maximumRenderTimeChange: Infinity,
    });

    // Set initial camera view (New York coordinates as example)
    viewer.camera.setView({
      destination: window.Cesium.Cartesian3.fromDegrees(-74.0707383, 40.7117244, 10000000),
      orientation: {
        heading: window.Cesium.Math.toRadians(0),
        pitch: window.Cesium.Math.toRadians(-90),
        roll: 0.0,
      },
    });

    // Callback to parent component
    if (onViewerInitialized) {
      onViewerInitialized(viewer);
    }

    // Cleanup on unmount
    return () => {
      if (viewer && !viewer.isDestroyed()) {
        viewer.destroy();
      }
    };
  }, [onViewerInitialized]);

  return <div ref={globeRef} id="cesiumContainer" className="cesium-container" />;
};

GlobeViewer.propTypes = {
  onViewerInitialized: PropTypes.func.isRequired,
};

export default GlobeViewer;
